sp_header_footer.link:
function(scope, element) {
	$(document).keyup(function(event) {
		if (event.which == 9) { //tab key handler + arrow keys
			if ($(event.target).parents(".dropdown-menu").length == 0) { //if we are NOT inside a dropdown...
				//close the dropdowns
				$(".dropdown").removeClass("open");
				$("[data-toggle='dropdown']").attr("aria-expanded", "false");
			}
		}
	});

	$(element).on('click.nav','.navbar-collapse.in',function(e) {
		var target = $(e.target);
		if (target.is('a')) {
			if (target.hasClass('sp-menu-has-items'))
				return; // menu item has a submenu, so just toggle it

			// collapse mobile nav if clicked an anchor
			$(this).removeClass('in').addClass('collapse');
			return;
		}

		if (target.parent().hasClass("toggle-dropdown")) {
			// collapse mobile nav if parent has toggle-dropdown class
			$(this).removeClass('in').addClass('collapse');
			return;
		}

		// if neither target nor parent is an anchor, do nothing
		if (!target.parent().is('a'))
			return;

		// collapse mobile nav if target is part of a submenu
		if (target.parents("ul.dropdown-menu").length > 0)
			$(this).removeClass('in').addClass('collapse');
	});
}

sp_header_footer.script:
// Defines the support queue ID that will be linked to by the Live Chat link in header
data.connect_support_queue_id = $sp.getValue('sp_chat_queue');
data.login_page = $sp.getValue('login_page');
data.profileBtnMsg = gs.getMessage("Press space to access user profile or logout");
var menu = $sp.getValue("sp_rectangle_menu");
data.menu = $sp.getWidgetFromInstance(menu);
if (data.menu && data.menu.data) {
	data.menu.data.replace = true;
	// Hide login if menu already has link to login
	data.hasLogin = false;
	if (data.menu.data.menu.items) {
		for(var i in data.menu.data.menu.items) {
			var item = data.menu.data.menu.items[i];
			if (item.type == 'page' && item.sp_page == data.login_page)
				data.hasLogin = true;
		}
	}
}

data.loginWidget = $sp.getWidgetFromInstance('login-modal');
data.typeahead = $sp.getWidgetFromInstance('typeahead-search');

data.clockOptions = {"zone": "US/Eastern"};

// Get Today's Date
var currentDate = new GlideDateTime();
var dayOfWeek = currentDate.getDayOfWeekLocalTime();
var monthOfYear = currentDate.getMonthLocalTime();

switch(dayOfWeek) {
	case 1:
		data.dayOfWeek = "Monday";
		break;
	case 2:
		data.dayOfWeek = "Tuesday";
		break;
	case 3:
		data.dayOfWeek = "Wednesday";
		break;
	case 4:
		data.dayOfWeek = "Thursday";
		break;
	case 5:
		data.dayOfWeek = "Friday";
		break;
	case 6:
		data.dayOfWeek = "Saturday";
		break;
	case 7:
		data.dayOfWeek = "Sunday";
		break;
}

switch(monthOfYear) {
	case 1:
		data.monthOfYear = "January";
		break;
	case 2:
		data.monthOfYear = "February";
		break;
	case 3:
		data.monthOfYear = "March";
		break;
	case 4:
		data.monthOfYear = "April";
		break;
	case 5:
		data.monthOfYear = "May";
		break;
	case 6:
		data.monthOfYear = "June";
		break;
	case 7:
		data.monthOfYear = "July";
		break;
	case 8:
		data.monthOfYear = "August";
		break;
	case 9:
		data.monthOfYear = "September";
		break;
	case 10:
		data.monthOfYear = "October";
		break;
	case 11:
		data.monthOfYear = "November";
		break;
	case 12:
		data.monthOfYear = "December";
		break;
}

data.dayOfMonth = currentDate.getDayOfMonthLocalTime();
