sp_widget.script:
(function() {

	var gr_project = new GlideRecord('x_143729_toggl_projects');
	gr_project.query();

	data.projects = [];
	while(gr_project.next()) {
		var project = {}

		$sp.getRecordDisplayValues(project, gr_project, 
			'name,billable,billable_rate_per_hour,sys_id');

		data.projects.push(project);
	}

})();
