sys_script_include.script:
var TimeEntries = Class.create();
TimeEntries.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// This script calculates the difference of the start and end times
	// of a time entry and sets the hours field based on that calculation
	getHours: function() {
		var startInput = this.getParameter('sysparm_startTime');
		var endInput = this.getParameter('sysparm_endTime');

		var startTime = new GlideTime();
		startTime.setDisplayValue(startInput);
		var endTime = new GlideTime();
		endTime.setDisplayValue(endInput);

		var hours = (endTime.getHourOfDayLocalTime() - startTime.getHourOfDayLocalTime());		
		var minutes = (endTime.getMinutesLocalTime() - startTime.getMinutesLocalTime());

		if(minutes < 10)
			return (hours + ":0" + minutes);
		else
			return (hours + ":" + minutes);
	},

	// Validates user input is in the 12 hour format
	validateStandardTime: function() {
		var timeInput = this.getParameter('sysparm_time');
		var hour = timeInput.slice(0, 2);

		if(parseInt(hour) < 1 || parseInt(hour) > 12) {
			return "false";
		}
		else {
			return "true";
		}

	},

    type: 'TimeEntries'
});
